mapper.update:
update canvas_info
set 
  canvas_name = #{canvasName,jdbcType=VARCHAR},
  describe_info = #{describeInfo,jdbcType=VARCHAR}

where id = #{id,jdbcType=BIGINT}

mapper.select:
	select

	from canvas_info where is_valid=1
    and  canvas_name = #{canvasName,jdbcType=VARCHAR}
    and  user_id = #{userId,jdbcType=BIGINT}

select a.id as materialInfoId, b.id as canvasId, a.png_url as pngUrl from material_info as a, canvas_info as b where a.canvas_info_id_private=b.id and a.is_valid=1
and a.id in #{listItem}
and a.id in ('')

select id,canvas_name 
	from canvas_info where is_valid = 1 
	 and user_id = #{userId,jdbcType=BIGINT}
