mapper.delete:
delete from material_info
where create_user_id = #{userId,jdbcType=BIGINT}
AND canvas_info_id_private = #{canvasId,jdbcType=BIGINT}

mapper.select:
select png_url as pngUrl from material_info 
where  canvas_info_id_private = #{canvasId}
and canvas_info_id_public = #{canvasId}
limit 20

